const body = $input.first().json.body || {};
const article = body.article || '';
const url = body.url || '';

if (!article && !url) {
  throw new Error('Article or URL is required');
}

return [{ json: { article, url } }];

parameters.query:
={{ 
  const article = ($json.article || '').replace(/'/g, "''");
  const url = ($json.url || '').replace(/'/g, "''");

  let where = '';
  if (article) {
    where = `n.article = '${article}'`;
  } else if (url) {
    where = `p.product_url LIKE '%${url}%'`;
  }

  `SELECT n.article, n.name, n.category, p.price, p.in_stock, p.product_url, p.updated_at FROM gsm05_nomenclature n JOIN gsm05_prices p ON p.nomenclature_id = n.id WHERE ${where} LIMIT 1;`
}}

