const body = $input.first().json.body;

const brandCode = body?.brand?.toLowerCase();
if (!brandCode) {
  throw new Error('Missing required field: brand');
}

return [{
  json: {
    brand_code: brandCode,
    max_models: body?.max_models || 0,
    delay_seconds: body?.delay_seconds || 2,
    save_to_db: body?.save_to_db !== false,
    use_proxy: body?.use_proxy !== false,
    use_mcp: body?.use_mcp !== false
  }
}];

const config = $('Validate Input').first().json;
const proxyResult = $('Get Proxy from Manager').first().json;
const mcpResult = $('Get Cookies from MCP').first().json;

// Proxy setup
let proxyUrl = null;
let proxyAddress = null;
if (config.use_proxy && proxyResult?.proxy) {
  proxyAddress = proxyResult.proxy;
  proxyUrl = `http://${proxyResult.proxy}`;
  console.log(`[PROXY] Using: ${proxyAddress}`);
} else {
  console.log('[PROXY] No proxy available or disabled');
}

// MCP cookies setup
let cookies = {};
let userAgent = 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/120.0.0.0 Safari/537.36';
let cookieHeader = '';

if (config.use_mcp && mcpResult?.success) {
  cookies = mcpResult.cookies || [];
  userAgent = mcpResult.user_agent || userAgent;
  cookieHeader = mcpResult.cookie_string || '';
  console.log(`[MCP] Got ${mcpResult.cookies_count || 0} cookies`);
} else {
  console.log('[MCP] No cookies or disabled');
}

return [{
  json: {
    ...config,
    proxy_url: proxyUrl,
    proxy_address: proxyAddress,
    cookies: cookies,
    cookie_header: cookieHeader,
    user_agent: userAgent,
    request_count: 0,
    success_count: 0,
    fail_count: 0
  }
}];

const cheerio = require('cheerio');

const config = $('Merge Proxy + Cookies').first().json;
const html = $input.first().json.data;
const $ = cheerio.load(html);

let brandInfo = null;

$('div.st-text a').each((i, el) => {
  const href = $(el).attr('href') || '';
  const text = $(el).text().trim();
  const match = text.match(/(.+?)\s*(\d+)\s*devices?/);

  if (match && href.includes('-phones-')) {
    const code = href.replace('-phones-', '_').replace('.php', '').split('_')[0].toLowerCase();
    if (code === config.brand_code) {
      brandInfo = {
        name: match[1].trim(),
        url: `https://www.gsmarena.com/${href}`,
        device_count: parseInt(match[2])
      };
      return false;
    }
  }
});

if (!brandInfo) {
  throw new Error(`Brand '${config.brand_code}' not found`);
}

return [{
  json: {
    ...config,
    brand_name: brandInfo.name,
    brand_url: brandInfo.url,
    device_count: brandInfo.device_count,
    current_page: 1,
    models: [],
    has_more: true,
    request_count: config.request_count + 1,
    success_count: config.success_count + 1
  }
}];

const state = $input.first().json;
let url;

if (state.current_page === 1) {
  url = state.brand_url;
} else {
  const match = state.brand_url.match(/-(\d+)\.php/);
  const brandId = match ? match[1] : null;
  const brandSlug = state.brand_url.split('/').pop().replace(`-${brandId}.php`, '');
  url = brandId ? `https://www.gsmarena.com/${brandSlug}-f-${brandId}-0-p${state.current_page}.php` : null;
}

if (!url) return [{ json: { ...state, has_more: false } }];
return [{ json: { ...state, fetch_url: url } }];

const cheerio = require('cheerio');
const state = $('Wait').first().json;
const html = $input.first().json.data;
const $ = cheerio.load(html);

const newModels = [];
$('div.makers ul li').each((i, el) => {
  const link = $(el).find('a');
  const href = link.attr('href') || '';
  const nameElem = link.find('span');
  const imgElem = link.find('img');

  if (nameElem.length && href) {
    const idMatch = href.match(/-(\d+)\.php/);
    newModels.push({
      brand: state.brand_name,
      name: nameElem.text().trim(),
      url: `https://www.gsmarena.com/${href}`,
      thumbnail: imgElem.attr('src') || '',
      brief_specs: imgElem.attr('title') || '',
      gsmarena_id: idMatch ? idMatch[1] : null
    });
  }
});

const navPages = $('div.nav-pages a');
const nextPage = state.current_page + 1;
let hasNext = navPages.toArray().some(el => ($(el).attr('href') || '').includes(`p${nextPage}`));

const allModels = [...state.models, ...newModels];
let shouldStop = state.max_models > 0 && allModels.length >= state.max_models;

return [{ 
  json: { 
    ...state, 
    models: allModels, 
    current_page: nextPage, 
    has_more: hasNext && newModels.length > 0 && !shouldStop,
    request_count: state.request_count + 1,
    success_count: state.success_count + 1
  } 
}];

// Prepare models for specs parsing
const state = $input.first().json;
let models = state.models;
if (state.max_models > 0) models = models.slice(0, state.max_models);

return models.map((m, i) => ({
  json: { 
    ...m, 
    index: i + 1, 
    total: models.length, 
    delay_seconds: state.delay_seconds, 
    save_to_db: state.save_to_db,
    proxy_url: state.proxy_url,
    proxy_address: state.proxy_address,
    user_agent: state.user_agent,
    cookie_header: state.cookie_header,
    request_count: state.request_count,
    success_count: state.success_count,
    fail_count: state.fail_count
  }
}));

const cheerio = require('cheerio');
const modelInfo = $('Wait Spec').first().json;
const html = $input.first().json.data;
const $ = cheerio.load(html);

function extractYear(t) { const m = (t||'').match(/20\d{2}/); return m ? parseInt(m[0]) : null; }
function extractWeight(t) { const m = (t||'').toLowerCase().match(/(\d+(?:\.\d+)?)\s*g\b/); return m ? parseInt(parseFloat(m[1])) : null; }
function extractBattery(t) { const m = (t||'').toLowerCase().match(/(\d+)\s*mah/); return m ? parseInt(m[1]) : null; }
function extractPrice(t) { const m = (t||'').toLowerCase().match(/(\d+(?:\.\d+)?)\s*eur/); return m ? parseFloat(m[1]) : null; }
function extractDisplay(t) { const m = (t||'').toLowerCase().match(/(\d+(?:\.\d+)?)\s*inch/); return m ? parseFloat(m[1]) : null; }
function extractCamera(t) { const m = (t||'').toLowerCase().match(/(\d+)\s*mp/); return m ? m[1] + ' MP' : null; }

const specs = { name: modelInfo.name, url: modelInfo.url, parsed_at: new Date().toISOString() };

$('table[cellspacing="0"]').each((i, table) => {
  let cat = null;
  $(table).find('tr').each((j, row) => {
    const th = $(row).find('th');
    if (th.length && th.attr('rowspan')) {
      cat = th.text().trim().toLowerCase().replace(/\s+/g, '_');
      if (!specs[cat]) specs[cat] = {};
    }
    const tds = $(row).find('td');
    if (tds.length >= 2 && cat) {
      const pn = $(tds[0]).text().trim().toLowerCase().replace(/\s+/g, '_');
      const pv = $(tds[1]).text().trim().replace(/\s+/g, ' ');
      if (pn && pv) specs[cat][pn] = pv;
    }
  });
});

const img = $('div.specs-photo-main img');
if (img.length) specs.image_url = img.attr('src') || '';
const pe = $('td[data-spec="price"]');
if (pe.length) specs.price = pe.text().trim();

const launch = specs.launch || {};
const body = specs.body || {};
const display = specs.display || {};
const platform = specs.platform || {};
const memory = specs.memory || {};
const mainCam = specs.main_camera || {};
const selfie = specs.selfie_camera || {};
const battery = specs.battery || {};
const network = specs.network || {};
const comms = specs.comms || {};
const sound = specs.sound || {};
const features = specs.features || {};
const misc = specs.misc || {};

const mcs = mainCam.single || mainCam.dual || mainCam.triple || mainCam.quad || '';
const scs = selfie.single || selfie.dual || '';
const chg = battery.charging || '';

let ip = null; const ipm = JSON.stringify(body).match(/IP\d+/); if (ipm) ip = ipm[0];
let rr = null; const rrm = (display.type || '').match(/(\d+)\s*Hz/); if (rrm) rr = rrm[1] + 'Hz';
let ram = null; const ramm = (memory.internal || '').match(/(\d+)\s*GB\s*RAM/); if (ramm) ram = ramm[1] + ' GB';
let wc = null; if (chg.toLowerCase().includes('wireless')) { const wcm = chg.toLowerCase().match(/(\d+W)\s*wireless/); wc = wcm ? wcm[1] : 'Yes'; }

const row = {
  brand: modelInfo.brand, model_name: specs.name, model_url: specs.url,
  image_url: specs.image_url || '', gsmarena_id: modelInfo.gsmarena_id,
  announced: launch.announced || '', release_status: launch.status || '', release_year: extractYear(launch.announced),
  dimensions: body.dimensions || '', weight: body.weight || '', weight_grams: extractWeight(body.weight),
  build: body.build || '', sim: body.sim || '', ip_rating: ip,
  display_type: display.type || '', display_size: display.size || '', display_size_inches: extractDisplay(display.size),
  display_resolution: display.resolution || '', display_protection: display.protection || '', refresh_rate: rr,
  os: platform.os || '', chipset: platform.chipset || '', cpu: platform.cpu || '', gpu: platform.gpu || '',
  ram: ram, storage: memory.internal || '', card_slot: memory.card_slot || '',
  main_camera_mp: extractCamera(mcs), main_camera_setup: mcs, main_camera_features: mainCam.features || '', main_camera_video: mainCam.video || '',
  selfie_camera_mp: extractCamera(scs), selfie_camera_setup: scs, selfie_camera_video: selfie.video || '',
  battery_capacity: battery.type || '', battery_capacity_mah: extractBattery(battery.type), battery_type: battery.type || '',
  charging_wired: chg, charging_wireless: wc,
  network_technology: network.technology || '', network_2g: network['2g_bands'] || '', network_3g: network['3g_bands'] || '',
  network_4g: network['4g_bands'] || '', network_5g: network['5g_bands'] || '',
  wlan: comms.wlan || '', bluetooth: comms.bluetooth || '', nfc: comms.nfc || '', gps: comms.positioning || '', usb: comms.usb || '', radio: comms.radio || '',
  loudspeaker: sound.loudspeaker || '', audio_jack: sound['3.5mm_jack'] || '', sensors: features.sensors || '',
  colors: misc.colors || '', models_list: misc.models || '', price: misc.price || specs.price || '', price_eur: extractPrice(misc.price || specs.price || ''),
  specs_json: JSON.stringify(specs), save_to_db: modelInfo.save_to_db,
  proxy_address: modelInfo.proxy_address,
  request_count: modelInfo.request_count + 1,
  success_count: modelInfo.success_count + 1,
  fail_count: modelInfo.fail_count
};

console.log(`[${modelInfo.index}/${modelInfo.total}] Parsed: ${row.model_name}`);
return [{ json: row }];

const items = $input.all();
const savedCount = items.filter(i => i.json.save_to_db).length;
const lastItem = items[items.length - 1]?.json || {};

return [{
  json: {
    success: true,
    brand: items[0]?.json?.brand,
    total_parsed: items.length,
    saved_to_db: savedCount,
    proxy_address: lastItem.proxy_address,
    request_count: lastItem.request_count || 0,
    success_count: lastItem.success_count || 0
  }
}];
