// Target brands to parse
const TARGET_BRANDS = [
  'apple', 'samsung', 'xiaomi', 'huawei', 'honor',
  'oppo', 'vivo', 'realme', 'oneplus', 'google',
  'motorola', 'nokia', 'sony', 'lg', 'asus',
  'nothing', 'zte', 'meizu', 'lenovo', 'poco'
];

// Return as items for loop
return TARGET_BRANDS.map(brand => ({
  json: { brand_code: brand }
}));

const cheerio = require('cheerio');

const html = $input.first().json.data;
const brandCode = $('Set Target Brands').first().json.brand_code;
const $ = cheerio.load(html);

let brandInfo = null;

$('div.st-text a').each((i, el) => {
  const href = $(el).attr('href') || '';
  const text = $(el).text().trim();

  // Match pattern: "Brand Name 123 devices"
  const match = text.match(/(.+?)\s*(\d+)\s*devices?/);

  if (match && href.includes('-phones-')) {
    const name = match[1].trim();
    const deviceCount = parseInt(match[2]);
    const code = href.replace('-phones-', '_').replace('.php', '').split('_')[0];

    if (code.toLowerCase() === brandCode.toLowerCase()) {
      brandInfo = {
        name: name,
        code: code,
        url: `https://www.gsmarena.com/${href}`,
        device_count: deviceCount
      };
      return false; // break
    }
  }
});

if (!brandInfo) {
  throw new Error(`Brand '${brandCode}' not found`);
}

return [{ json: brandInfo }];

// Initialize pagination state
const brandInfo = $input.first().json;

return [{
  json: {
    ...brandInfo,
    current_page: 1,
    models: [],
    has_more: true
  }
}];

const state = $input.first().json;
let url;

if (state.current_page === 1) {
  url = state.url;
} else {
  // Extract brand ID from URL
  const match = state.url.match(/-(\d+)\.php/);
  const brandId = match ? match[1] : null;
  const brandSlug = state.url.split('/').pop().replace(`-${brandId}.php`, '');

  if (brandId && brandSlug) {
    url = `https://www.gsmarena.com/${brandSlug}-f-${brandId}-0-p${state.current_page}.php`;
  } else {
    // Can't paginate, stop
    return [{ json: { ...state, has_more: false } }];
  }
}

return [{ json: { ...state, fetch_url: url } }];

const cheerio = require('cheerio');

const prevState = $('Build Page URL').first().json;
const html = $input.first().json.data;
const $ = cheerio.load(html);

const newModels = [];

$('div.makers ul li').each((i, el) => {
  const link = $(el).find('a');
  const href = link.attr('href') || '';
  const nameElem = link.find('span');
  const imgElem = link.find('img');

  if (nameElem.length && href) {
    const modelName = nameElem.text().trim();
    const briefSpecs = imgElem.attr('title') || imgElem.attr('alt') || '';

    // Extract GSMArena ID from URL
    const idMatch = href.match(/-(\d+)\.php/);
    const gsmId = idMatch ? idMatch[1] : null;

    newModels.push({
      brand: prevState.name,
      name: modelName,
      url: `https://www.gsmarena.com/${href}`,
      brief_specs: briefSpecs,
      gsmarena_id: gsmId
    });
  }
});

// Check for next page
const navPages = $('div.nav-pages a');
const nextPage = prevState.current_page + 1;
let hasNext = false;

navPages.each((i, el) => {
  const href = $(el).attr('href') || '';
  if (href.includes(`p${nextPage}`)) {
    hasNext = true;
    return false;
  }
});

// Combine models
const allModels = [...prevState.models, ...newModels];

console.log(`Page ${prevState.current_page}: found ${newModels.length} models, total: ${allModels.length}`);

return [{
  json: {
    ...prevState,
    models: allModels,
    current_page: nextPage,
    has_more: hasNext && newModels.length > 0
  }
}];

// Convert models array to individual items for processing
const state = $input.first().json;

console.log(`Brand ${state.name}: ${state.models.length} models to parse`);

return state.models.map((model, index) => ({
  json: {
    ...model,
    index: index + 1,
    total: state.models.length
  }
}));

const cheerio = require('cheerio');

const modelInfo = $('Process Models One by One').first().json;
const html = $input.first().json.data;
const $ = cheerio.load(html);

// Helper functions
function extractNumber(text, pattern = /(\d+(?:\.\d+)?)/g) {
  if (!text) return null;
  const match = text.replace(/,/g, '').match(pattern);
  return match ? parseFloat(match[0]) : null;
}

function extractYear(text) {
  if (!text) return null;
  const match = text.match(/20\d{2}/);
  return match ? parseInt(match[0]) : null;
}

function extractWeight(text) {
  if (!text) return null;
  const match = text.toLowerCase().match(/(\d+(?:\.\d+)?)\s*g\b/);
  return match ? parseInt(parseFloat(match[1])) : null;
}

function extractBattery(text) {
  if (!text) return null;
  const match = text.toLowerCase().match(/(\d+)\s*mah/);
  return match ? parseInt(match[1]) : null;
}

function extractPrice(text) {
  if (!text) return null;
  const match = text.toLowerCase().match(/(\d+(?:\.\d+)?)\s*eur/);
  return match ? parseFloat(match[1]) : null;
}

function extractDisplay(text) {
  if (!text) return null;
  const match = text.toLowerCase().match(/(\d+(?:\.\d+)?)\s*inch/);
  return match ? parseFloat(match[1]) : null;
}

function extractCamera(text) {
  if (!text) return null;
  const match = text.toLowerCase().match(/(\d+)\s*mp/);
  return match ? match[1] + ' MP' : null;
}

// Parse specs
const specs = {
  name: modelInfo.name,
  url: modelInfo.url,
  parsed_at: new Date().toISOString()
};

// Parse specs tables
$('table[cellspacing="0"]').each((i, table) => {
  let category = null;

  $(table).find('tr').each((j, row) => {
    const th = $(row).find('th');
    if (th.length && th.attr('rowspan')) {
      category = th.text().trim().toLowerCase().replace(/\s+/g, '_');
      if (!specs[category]) specs[category] = {};
    }

    const tds = $(row).find('td');
    if (tds.length >= 2 && category) {
      const paramName = $(tds[0]).text().trim().toLowerCase().replace(/\s+/g, '_');
      let paramValue = $(tds[1]).text().trim().replace(/\s+/g, ' ');

      if (paramName && paramValue) {
        specs[category][paramName] = paramValue;
      }
    }
  });
});

// Image
const img = $('div.specs-photo-main img');
if (img.length) {
  specs.image_url = img.attr('src') || '';
}

// Price
const priceElem = $('td[data-spec="price"]');
if (priceElem.length) {
  specs.price = priceElem.text().trim();
}

// Build database row
const launch = specs.launch || {};
const body = specs.body || {};
const display = specs.display || {};
const platform = specs.platform || {};
const memory = specs.memory || {};
const mainCam = specs.main_camera || {};
const selfie = specs.selfie_camera || {};
const battery = specs.battery || {};
const network = specs.network || {};
const comms = specs.comms || {};
const sound = specs.sound || {};
const features = specs.features || {};
const misc = specs.misc || {};
const eu = specs.eu_label || {};

const mainCamSetup = mainCam.single || mainCam.dual || mainCam.triple || mainCam.quad || '';
const selfieCamSetup = selfie.single || selfie.dual || '';
const charging = battery.charging || '';

// IP rating
let ipRating = null;
const ipMatch = JSON.stringify(body).match(/IP\d+/);
if (ipMatch) ipRating = ipMatch[0];

// Refresh rate
let refreshRate = null;
const hzMatch = (display.type || '').match(/(\d+)\s*Hz/);
if (hzMatch) refreshRate = hzMatch[1] + 'Hz';

// RAM
let ram = null;
const ramMatch = (memory.internal || '').match(/(\d+)\s*GB\s*RAM/);
if (ramMatch) ram = ramMatch[1] + ' GB';

// Wireless charging
let wirelessCharging = null;
if (charging.toLowerCase().includes('wireless')) {
  const wMatch = charging.toLowerCase().match(/(\d+W)\s*wireless/);
  wirelessCharging = wMatch ? wMatch[1] : 'Yes';
}

const row = {
  brand: modelInfo.brand,
  model_name: specs.name,
  model_url: specs.url,
  image_url: specs.image_url || '',
  gsmarena_id: modelInfo.gsmarena_id,

  // Launch
  announced: launch.announced || '',
  release_status: launch.status || '',
  release_year: extractYear(launch.announced),

  // Body
  dimensions: body.dimensions || '',
  weight: body.weight || '',
  weight_grams: extractWeight(body.weight),
  build: body.build || '',
  sim: body.sim || '',
  ip_rating: ipRating,

  // Display
  display_type: display.type || '',
  display_size: display.size || '',
  display_size_inches: extractDisplay(display.size),
  display_resolution: display.resolution || '',
  display_protection: display.protection || '',
  refresh_rate: refreshRate,

  // Platform
  os: platform.os || '',
  chipset: platform.chipset || '',
  cpu: platform.cpu || '',
  gpu: platform.gpu || '',

  // Memory
  ram: ram,
  storage: memory.internal || '',
  card_slot: memory.card_slot || '',

  // Camera
  main_camera_mp: extractCamera(mainCamSetup),
  main_camera_setup: mainCamSetup,
  main_camera_features: mainCam.features || '',
  main_camera_video: mainCam.video || '',
  selfie_camera_mp: extractCamera(selfieCamSetup),
  selfie_camera_setup: selfieCamSetup,
  selfie_camera_video: selfie.video || '',

  // Battery
  battery_capacity: battery.type || '',
  battery_capacity_mah: extractBattery(battery.type),
  battery_type: battery.type || '',
  charging_wired: charging,
  charging_wireless: wirelessCharging,

  // Network
  network_technology: network.technology || '',
  network_2g: network['2g_bands'] || '',
  network_3g: network['3g_bands'] || '',
  network_4g: network['4g_bands'] || '',
  network_5g: network['5g_bands'] || '',

  // Comms
  wlan: comms.wlan || '',
  bluetooth: comms.bluetooth || '',
  nfc: comms.nfc || '',
  gps: comms.positioning || '',
  usb: comms.usb || '',
  radio: comms.radio || '',

  // Sound
  loudspeaker: sound.loudspeaker || '',
  audio_jack: sound['3.5mm_jack'] || '',

  // Features
  sensors: features.sensors || '',

  // Misc
  colors: misc.colors || '',
  models_list: misc.models || '',
  price: misc.price || specs.price || '',
  price_eur: extractPrice(misc.price || specs.price || ''),

  // EU Label
  eu_energy_class: eu.energy || '',
  eu_battery_endurance: eu.battery || '',
  eu_repairability: eu.repairability || '',

  // Full specs JSON
  specs_json: JSON.stringify(specs)
};

console.log(`[${modelInfo.index}/${modelInfo.total}] Parsed: ${row.model_name}`);

return [{ json: row }];

// Log completion
const model = $input.first().json;
console.log(`Saved: ${model.model_name}`);
return $input.all();
