const body = $input.first().json.body || {};
const article = body.article || '';
if (!article) throw new Error('Article required');
return [{ json: { article } }];

const results = $input.all().map(i => i.json);
const article = $('Validate').first().json.article;
if (!results.length || !results[0].article) return [{ json: { success: false, error: 'Not found', article } }];
return [{ json: { success: true, shop: 'liberti', article: results[0].article, name: results[0].name, category: results[0].category, prices_by_city: results.map(r => ({ city: r.city, price: parseFloat(r.price) || 0, in_stock: r.in_stock, updated_at: r.updated_at })) } }];
