const body = $input.first().json.body || {};
const article = body.article || body.sku || '';
const url = body.url || '';

if (!article && !url) {
  throw new Error('Article or URL is required');
}

return [{
  json: { article, url }
}];

parameters.query:
={{ 
  const article = ($json.article || '').replace(/'/g, "''");
  const url = ($json.url || '').replace(/'/g, "''");

  let where = '';
  if (article) {
    where = `n.article = '${article}'`;
  } else if (url) {
    where = `p.product_url LIKE '%${url}%'`;
  }

  `SELECT n.article, n.name, n.category, n.brand, p.price, p.old_price, p.quantity, p.in_stock, p.product_url, p.updated_at FROM orizhka_nomenclature n JOIN orizhka_prices p ON p.nomenclature_id = n.id WHERE ${where} LIMIT 1;`
}}

parameters.jsCode:
const result = $input.first().json;
const input = $('Validate Input').first().json;

if (!result || !result.article) {
  return [{
    json: {
      success: false,
      error: 'Product not found',
      article: input.article,
      url: input.url
    }
  }];
}

return [{
  json: {
    success: true,
    shop: 'orizhka',
    article: result.article,
    name: result.name,
    category: result.category,
    brand: result.brand,
    price: parseFloat(result.price) || 0,
    old_price: parseFloat(result.old_price) || null,
    quantity: result.quantity || 0,
    in_stock: result.in_stock,
    url: result.product_url,
    updated_at: result.updated_at
  }
}];
