parameters.query:
DELETE FROM public.profi_price_for_update
WHERE NOT (
  (brand_attr = '3. АКСЕССУАРЫ' AND model_attr = 'АКБ ДЛЯ МОБИЛЬНОЙ ТЕХНИКИ')
  OR brand_attr IN ('2. ЗАПЧАСТИ ДЛЯ СОТОВЫХ','1. ЗАПЧАСТИ ДЛЯ APPLE')
);

value.price_rub:
={{
  $json["Цена"]
    ? parseFloat(
        $json["Цена"]
          .replace(/[^\d.,]/g, '')   // убираем всё, кроме цифр, запятой и точки
          .replace(',', '.')         // заменяем запятую на точку
      )
    : null
}}
