const body = $input.first().json.body || {};

// Categories
const CATEGORIES = {
  'displei': 'Дисплеи',
  'zadnie-kryshki': 'Задние крышки',
  'akkumulyatory': 'Аккумуляторы',
  'aksessuary': 'Аксессуары',
  'shlejfy-platy': 'Платы зарядки',
  'smart-chasy': 'Смарт-часы'
};

// Outlets (5 locations in Moscow)
const OUTLETS = {
  'savelovskiy': 'ТК Савеловский',
  'sklad-savelovskiy': 'Склад Савеловский',
  'vernadskogo': 'Проспект Вернадского',
  'mitino': 'Митино',
  'tekstilshiki': 'Текстильщики'
};

const category = body.category || 'displei';
const outlet = body.outlet || 'savelovskiy';

return [{
  json: {
    category_slug: category,
    category_name: CATEGORIES[category] || category,
    outlet_slug: outlet,
    outlet_name: OUTLETS[outlet] || outlet,
    max_pages: body.max_pages || 50,
    use_proxy: body.use_proxy !== false,
    save_to_db: body.save_to_db !== false,
    delay_seconds: body.delay_seconds || 1
  }
}];

const config = $('Validate Input').first().json;
const proxyResult = $input.first().json;

let proxyUrl = null;
let proxyAddress = null;

if (config.use_proxy && proxyResult?.proxy) {
  proxyAddress = proxyResult.proxy;
  proxyUrl = `http://${proxyResult.proxy}`;
}

return [{
  json: {
    ...config,
    proxy_url: proxyUrl,
    proxy_address: proxyAddress,
    base_url: 'https://lcd-stock.ru',
    current_page: 1,
    products: []
  }
}];

const state = $input.first().json;
const url = `${state.base_url}/catalog/${state.category_slug}/${state.outlet_slug}/?page=${state.current_page}`;
return [{ json: { ...state, fetch_url: url } }];

const cheerio = require('cheerio');
const state = $('Wait').first().json;
const response = $input.first().json;

if (!response.data || response.data.length < 1000) {
  return [{ json: { ...state, current_page: state.max_pages + 1 } }];
}

const $ = cheerio.load(response.data);
const newProducts = [];

$('.product-card, .catalog-item, .item').each((i, el) => {
  try {
    const $item = $(el);
    const product = {};

    const link = $item.find('a[href*="/product/"]').first();
    if (link.length) {
      product.name = link.text().trim();
      product.url = state.base_url + link.attr('href');
    }

    const skuElem = $item.find('.sku, .article');
    product.article = skuElem.length ? skuElem.text().trim() : '';

    const priceElem = $item.find('.price, .cost');
    const priceText = priceElem.text().replace(/[^\d]/g, '');
    product.price = priceText ? parseInt(priceText) : 0;

    const stockElem = $item.find('.stock, .availability');
    product.in_stock = stockElem.length > 0 && !stockElem.text().toLowerCase().includes('нет');

    product.category = state.category_name;
    product.outlet = state.outlet_slug;

    if (product.name && product.article) {
      newProducts.push(product);
    }
  } catch (e) {}
});

const hasNextPage = $('a.next, .pagination a[rel="next"]').length > 0;

return [{
  json: {
    ...state,
    products: [...state.products, ...newProducts],
    current_page: hasNextPage && newProducts.length > 0 ? state.current_page + 1 : state.max_pages + 1
  }
}];

