  const products = $('Check-save').first().json.products;
  const values = products.filter(p => p.article).map(p => {
    const name = (p.name || '').replace(/'/g, "''").slice(0, 500);
    const article = (p.article || '').replace(/'/g, "''");
    const category = (p.category || '').replace(/'/g, "''");
    return `('${name}', '${article}', '${category}', NOW(), NOW())`;
  }).join(',');
  if (!values) return 'SELECT 1';
  `INSERT INTO lcdstock_nomenclature_v2 (name, article, category, first_seen_at, updated_at) VALUES ${values} ON CONFLICT (article) DO UPDATE SET name = EXCLUDED.name, category = EXCLUDED.category, updated_at = NOW();`;
}}

={{ 
  const products = $('Check-save').first().json.products;
  const outlet = $('Check-save').first().json.outlet_slug;
  const queries = products.filter(p => p.article).map(p => {
    const article = (p.article || '').replace(/'/g, "''");
    const url = (p.url || '').replace(/'/g, "''");
    return `INSERT INTO lcdstock_prices_v2 (nomenclature_id, outlet_id, price, in_stock, product_url, updated_at) SELECT n.id, o.id, ${p.price || 0}, ${p.in_stock ? 'true' : 'false'}, '${url}', NOW() FROM lcdstock_nomenclature_v2 n, outlets o WHERE n.article = '${article}' AND o.code = 'lcdstock-${outlet}' ON CONFLICT (nomenclature_id, outlet_id) DO UPDATE SET price = EXCLUDED.price, in_stock = EXCLUDED.in_stock, product_url = EXCLUDED.product_url, updated_at = NOW();`;
  }).join('');
  queries || 'SELECT 1';
}}
