const body = $input.first().json.body || {};
const article = body.article || '';
const url = body.url || '';
if (!article && !url) throw new Error('Article or URL required');
return [{ json: { article, url } }];

const results = $input.all().map(i => i.json);
const input = $('Validate').first().json;
if (!results.length || !results[0].article) return [{ json: { success: false, error: 'Not found', article: input.article, url: input.url } }];
return [{ json: { success: true, shop: 'memstech', article: results[0].article, name: results[0].name, category: results[0].category, prices_by_city: results.map(r => ({ city: r.city, price: parseFloat(r.price) || 0, in_stock: r.in_stock, url: r.product_url, updated_at: r.updated_at })) } }];
