const body = $input.first().json.body || {};
return [{
  json: {
    save_to_db: body.save_to_db !== false
  }
}];

const config = $('Validate Input').first().json;
const data = $input.first().json;

const products = (data.products || []).map(p => ({
  article: p.article || p.code || '',
  name: p.name || '',
  price: p.price || 0,
  category: p.category || '',
  in_stock: (p.stock || 0) > 0 || p.available === true,
  quantity: p.stock || 0,
  url: '',
  image: p.imageURL || ''
}));

return [{
  json: {
    products: products,
    total: data.size || products.length,
    save_to_db: config.save_to_db
  }
}];

