={{ 
  const products = $('Process Products').first().json.products;

  const values = products.filter(p => p.article).map(p => {
    const name = (p.name || '').replace(/'/g, "''").slice(0, 500);
    const article = (p.article || '').replace(/'/g, "''");
    const category = (p.category || '').replace(/'/g, "''");
    return `('${article}', '${name}', '${category}', NOW(), NOW())`;
  }).join(',');

  if (!values) return 'SELECT 1';

  `INSERT INTO nomenclature (article, name, category_raw, first_seen_at, updated_at) VALUES ${values} ON CONFLICT (article) DO UPDATE SET name = EXCLUDED.name, category_raw = EXCLUDED.category_raw, updated_at = NOW();`;
}}

={{ 
  const products = $('Process Products').first().json.products;

  const queries = products.filter(p => p.article).map(p => {
    const article = (p.article || '').replace(/'/g, "''");
    const price = p.price || 0;
    const inStock = p.in_stock ? 'true' : 'false';

    return `INSERT INTO current_prices (nomenclature_id, outlet_id, price, in_stock, updated_at) SELECT n.id, o.id, ${price}, ${inStock}, NOW() FROM nomenclature n, outlets o WHERE n.article = '${article}' AND o.code = 'moysklad-naffas' ON CONFLICT (nomenclature_id, outlet_id) DO UPDATE SET price = EXCLUDED.price, in_stock = EXCLUDED.in_stock, updated_at = NOW();`;
  }).join('');

  queries || 'SELECT 1';
}}

