// === Prepare Prompt ===
// Формируем system prompt и user prompt для классификации товара.
// Модель можно поменять здесь:
const model = 'gpt-4o-mini';

const input = $input.first().json.body;
const name = input.name || '';
const article = input.article || '';

const systemPrompt = `Ты — классификатор товаров для магазина запчастей мобильных устройств.
Твоя задача — определить, является ли товар запасной частью для мобильного устройства.

Запчасти: дисплеи, тачскрины, батареи, камеры, шлейфы, динамики, микрофоны, разъёмы, корпуса, стёкла, защитные плёнки, матрицы, контроллеры, микросхемы и т.д.

НЕ запчасти: чехлы, кабели, зарядки, наушники, аксессуары, инструменты, расходники для ремонта (клей, скотч).

Ответь строго в формате JSON:
{
  "is_spare_part": true/false,
  "nomenclature_type": "тип товара (display, battery, camera, flex, speaker, connector, housing, glass, screen_protector, ic, other)",
  "confidence": 0.0-1.0
}`;

const userPrompt = `Товар: "${name}"
Артикул: "${article}"

Это запчасть для мобильного устройства?`;

return [{
  json: {
    model,
    systemPrompt,
    userPrompt,
    originalInput: input
  }
}];

// === Parse Response ===
// Извлекаем JSON из ответа OpenAI и валидируем поля.

const response = $input.first().json;

try {
  const content = response.choices[0].message.content;
  const parsed = JSON.parse(content);

  return [{
    json: {
      is_spare_part: parsed.is_spare_part === true,
      nomenclature_type: parsed.nomenclature_type || null,
      confidence: typeof parsed.confidence === 'number' ? parsed.confidence : 0.5
    }
  }];
} catch (error) {
  // Fallback: если AI не вернул валидный JSON
  return [{
    json: {
      is_spare_part: true,
      nomenclature_type: null,
      confidence: 0.0
    }
  }];
}
