// === Prepare Brands List ===
// Форматируем список существующих брендов в компактный вид для промпта.
// Это отдельная нода, чтобы можно было видеть и менять формат.

const input = $input.first().json.body;
const brandRaw = input.brand_raw || '';
const allBrands = input.all_brands || [];

// Компактный формат: "id:name" по одному на строку
const brandsText = allBrands
  .map(b => `${b.id}: ${b.name}`)
  .join('\n');

const brandsCount = allBrands.length;

return [{
  json: {
    brandRaw,
    brandsText,
    brandsCount,
    allBrands
  }
}];

// === Prepare Prompt ===
// Формируем промпт для валидации бренда.
const model = 'gpt-4o-mini';

const { brandRaw, brandsText, brandsCount } = $input.first().json;

const systemPrompt = `Ты — эксперт по брендам мобильных устройств.
Тебе дан бренд, извлечённый AI из названия товара, и список всех существующих брендов в справочнике (${brandsCount} шт).

Задача: определить, есть ли этот бренд в списке (возможно с опечаткой или другим написанием).

Примеры совпадений:
- "Aple" → "Apple" (опечатка)
- "SAMSUNG" → "Samsung" (регистр)
- "Хуавей" → "Huawei" (транслитерация)
- "Редми" → "Xiaomi" (суббренд)

Если бренд НЕ найден в списке — это новый бренд.

Ответь строго в формате JSON:
{
  "is_new": true/false,
  "match_id": число или null (ID из списка, если найдено совпадение),
  "match": "точное имя из списка" или null,
  "confidence": 0.0-1.0,
  "reasoning": "краткое объяснение на русском"
}`;

const userPrompt = `Бренд для проверки: "${brandRaw}"

Список существующих брендов (id: name):
${brandsText}`;

return [{
  json: {
    model,
    systemPrompt,
    userPrompt
  }
}];

// === Parse Response ===
// Парсим ответ AI и валидируем match_id.

const response = $input.first().json;

try {
  const content = response.choices[0].message.content;
  const parsed = JSON.parse(content);

  return [{
    json: {
      is_new: parsed.is_new === true,
      match_id: typeof parsed.match_id === 'number' ? parsed.match_id : null,
      match: parsed.match || null,
      confidence: typeof parsed.confidence === 'number' ? parsed.confidence : 0.5,
      reasoning: parsed.reasoning || ''
    }
  }];
} catch (error) {
  return [{
    json: {
      is_new: true,
      match_id: null,
      match: null,
      confidence: 0.0,
      reasoning: 'Failed to parse AI response: ' + error.message
    }
  }];
}
