const body = $input.first().json.body || {};
const article = body.article || '';
const url = body.url || '';
if (!article && !url) throw new Error('Article or URL required');
return [{ json: { article, url } }];

const result = $input.first().json;
const input = $('Validate').first().json;
if (!result || !result.article) return [{ json: { success: false, error: 'Not found', article: input.article, url: input.url } }];
return [{ json: { success: true, shop: 'signal23', article: result.article, name: result.name, category: result.category, price: parseFloat(result.price) || 0, in_stock: result.in_stock, url: result.product_url, updated_at: result.updated_at } }];
